jQuery(document).ready(function ($) {

    function Init() {
        setSaleAjax();
    }

    Init();

    function setSaleAjax() {
        let $saleContainers = $('.acym__campaign__sale');

        if ($saleContainers.length === 0) return;

        $saleContainers.each(index => {
            let data = {
                ctrl: 'campaigns',
                task: 'getSalesByMailId',
                id: $saleContainers[index].getAttribute('data-acym-mail')
            };

            acym_helper.get(ACYM_AJAX_URL, data).then(response => {
                $saleContainers[index].innerHTML = response.data.income;
            });
        });
    }
});